import React from 'react';
import { Link } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = () => {
  return (
    <div className="landing-container">
      <header className="landing-header">
        <h1>Bienvenido a Roki</h1>
        <p>Tu plataforma de streaming de música favorita</p>
      </header>
      <div className="landing-buttons">
        <Link to="/signup" className="btn">Regístrate</Link>
        <Link to="/login" className="btn">Iniciar Sesión</Link>
      </div>
      <div className="features">
        <h2>Características</h2>
        <ul>
          <li>Escucha música sin anuncios</li>
          <li>Crea y comparte playlists</li>
          <li>Acceso a millones de canciones</li>
        </ul>
      </div>
      <footer className="landing-footer">
        <p>© 2024 Roki. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
};

export default LandingPage;
